import React, { useState } from 'react';
import TranscriptionPanel from './components/TranscriptionPanel/TranscriptionPanel';
import QASection from './components/QASection/QASection';
import DocumentUploader from './components/DocumentUploader/DocumentUploader';
import DashboardLayout from './components/DashboardLayout/DashboardLayout';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import DocumentList from './components/DocumentList';
import Home from './components/Home';
import ErrorBoundary from './components/ErrorBoundary';
import './App.css';

function App() {
  const [documents, setDocuments] = useState([]);

  // Handle document upload completion
  const handleUploadComplete = (newDocument) => {
    setDocuments([...documents, newDocument]);
  };

  return (
    <BrowserRouter>
      <div className="app-container">
        {/* Navigation Bar */}
        <nav className="navbar">
          <ul className="nav-list">
            <li className="nav-item">
              <Link to="/" className="nav-link">Home</Link>
            </li>
            <li className="nav-item">
              <Link to="/upload" className="nav-link">Upload Document</Link>
            </li>
            <li className="nav-item">
              <Link to="/documents" className="nav-link">View Documents</Link>
            </li>
          </ul>
        </nav>

        {/* Main Content */}
        <main className="main-content">
          <ErrorBoundary>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route 
                path="/upload" 
                element={
                  <DocumentUploader 
                    onUpload={handleUploadComplete} 
                  />
                } 
              />
              <Route 
                path="/documents" 
                element={<DocumentList documents={documents} />} 
              />
              <Route path="*" element={<NotFound />} />
            </Routes>
          </ErrorBoundary>
        </main>
      </div>
    </BrowserRouter>
  );
}

// 404 Component
function NotFound() {
  return (
    <div className="not-found">
      <h2>404 - Page Not Found</h2>
      <p>The page you're looking for doesn't exist.</p>
      <Link to="/" className="return-home">Return to Home</Link>
    </div>
  );
}

export default App;
