import React, { useState } from 'react';
import TranscriptionPanel from './components/TranscriptionPanel/TranscriptionPanel';
import QASection from './components/QASection/QASection';
import DocumentUploader from './components/DocumentUploader/DocumentUploader';
import DashboardLayout from './components/DashboardLayout/DashboardLayout';
import NavigationDrawer from './components/NavigationDrawer/NavigationDrawer';
import AssessmentDisplay from './components/AssessmentDisplay/AssessmentDisplay';

const App = () => {
  const [selectedTab, setSelectedTab] = useState('transcription');

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  return (
    <DashboardLayout>
      <NavigationDrawer />
      <main role="main" className="flex-grow">
        <>
          {selectedTab === 'transcription' && <TranscriptionPanel onTabChange={handleTabChange} />}
          {selectedTab === 'qa' && <QASection onTabChange={handleTabChange} />}
          {selectedTab === 'document-uploader' && <DocumentUploader onTabChange={handleTabChange} />}
        </>
      </main>
      <AssessmentDisplay />
    </DashboardLayout>
  );
};

export default App;
